// src/components/TodoList.tsx (modificar)
import React from 'react';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import TodoItem from './TodoItem';
import { useTodos } from '../hooks/useTodos';
import { useTodoContext } from '../contexts/TodoContext';
import { TodoStatus } from '../types/todo';

function TodoList(): JSX.Element {
  const todos = useTodos();
  const {
    filter,
    setFilter,
    remainingCount,
    completedCount,
    clearCompleted,
    reorderTodos
  } = useTodoContext();

  // Função para alternar o filtro
  const handleFilterChange = (newFilter: TodoStatus): void => {
    setFilter(newFilter);
  };

  // Função para lidar com o fim do arrasto
  const handleDragEnd = (result: DropResult): void => {
    // Ignorar se não foi solto em uma área válida
    if (!result.destination) {
      return;
    }

    // Ignorar se a posição não mudou
    if (
      result.destination.index === result.source.index
    ) {
      return;
    }

    // Reordenar as tarefas
    reorderTodos(result.source.index, result.destination.index);
  };

  return (
    <div className="todo-list-container">
      {todos.length > 0 ? (
        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="todo-list">
            {(provided) => (
              <ul
                className="todo-list"
                {...provided.droppableProps}
                ref={provided.innerRef}
              >
                {todos.map((todo, index) => (
                  <Draggable
                    key={todo.id}
                    draggableId={todo.id}
                    index={index}
                  >
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <TodoItem todo={todo} />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </ul>
            )}
          </Droppable>
        </DragDropContext>
      ) : (
        <p className="empty-message">
          {filter === 'all'
            ? 'Nenhuma tarefa adicionada.'
            : filter === 'active'
            ? 'Nenhuma tarefa ativa.'
            : 'Nenhuma tarefa concluída.'}
        </p>
      )}

      <div className="todo-footer">
        <span className="todo-count">
          {remainingCount} {remainingCount === 1 ? 'tarefa' : 'tarefas'} restante
          {remainingCount !== 1 ? 's' : ''}
        </span>

        <div className="todo-filters">
          <button
            className={filter === 'all' ? 'active' : ''}
            onClick={() => handleFilterChange('all')}
          >
            Todas
          </button>
          <button
            className={filter === 'active' ? 'active' : ''}
            onClick={() => handleFilterChange('active')}
          >
            Ativas
          </button>
          <button
            className={filter === 'completed' ? 'active' : ''}
            onClick={() => handleFilterChange('completed')}
          >
            Concluídas
          </button>
        </div>

        {completedCount > 0 && (
          <button className="clear-completed" onClick={clearCompleted}>
            Limpar concluídas
          </button>
        )}
      </div>
    </div>
  );
}

