// src/components/TranslatedForm.tsx
import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

interface FormData {
  name: string;
  email: string;
  message: string;
}

export const TranslatedForm: React.FC = () => {
  const { t } = useTranslation();
  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    message: ''
  });
  const [errors, setErrors] = useState<Partial<Record<keyof FormData, string>>>({});
  
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    // Limpar erro quando o usuário começa a digitar
    if (errors[name as keyof FormData]) {
      setErrors(prev => ({ ...prev, [name]: undefined }));
    }
  };
  
  const validate = (): boolean => {
    const newErrors: Partial<Record<keyof FormData, string>> = {};
    
    if (!formData.name.trim()) {
      newErrors.name = t('form.errors.required');
    }
    
    if (!formData.email.trim()) {
      newErrors.email = t('form.errors.required');
    } else if (!/\\S+@\\S+\\.\\S+/.test(formData.email)) {
      newErrors.email = t('form.errors.invalidEmail');
    }
    
    if (!formData.message.trim()) {
      newErrors.message = t('form.errors.required');
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    if (validate()) {
      // Enviar formulário
      console.log('Form submitted:', formData);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} noValidate>
      <div className="form-field">
        <label htmlFor="name">{t('form.name')}</label>
        <input
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          aria-invalid={!!errors.name}
        />
        {errors.name && <div className="error">{errors.name}</div>}
      </div>
      
      <div className="form-field">
        <label htmlFor="email">{t('form.email')}</label>
        <input
          id="email"
          name="email"
          type="email"
          value={formData.email}
          onChange={handleChange}
          aria-invalid={!!errors.email}
        />
        {errors.email && <div className="error">{errors.email}</div>}
      </div>
      
      <div className="form-field">
        <label htmlFor="message">{t('form.message')}</label>
        <textarea
          id="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
          aria-invalid={!!errors.message}
        />
        {errors.message && <div className="error">{errors.message}</div>}
      </div>
      
      <button type="submit">{t('form.submit')}</button>
    </form>
  );
};
